class Usuario_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }

    /*
     * Get usuario by id
     */
    function get_usuario($id)
    {
        return $this->db->get_where('usuario',array('id'=>$id))->row_array();
    }

    /*
     * Get all usuario
     */
    function get_all_usuario()
    {
        $this->db->select('usu.*, pe.nombres, pe.apellido_paterno, pe.apellido_materno, tu.nombre as nombre_tipo_usuario');
        $this->db->from('usuario usu, persona pe, tipo_usuario tu');
        $this->db->where('usu.persona_id = pe.id AND usu.tipo_usuario_id = tu.id');
        $this->db->order_by('usu.id', 'desc');
        return $this->db->get()->result_array();
    }

    /*
     * function to add new usuario
     */
    function add_usuario($params)
    {
        $this->db->insert('usuario',$params);
        return $this->db->insert_id();
    }

    /*
     * function to update usuario
     */
    function update_usuario($id,$params)
    {
        $this->db->where('id',$id);
        return $this->db->update('usuario',$params);
    }

    /*
     * function to delete usuario
     */
    function delete_usuario($id)
    {
        return $this->db->delete('usuario',array('id'=>$id));
    }

    function listarConductores(){
      $this->db->select('usu.*, pe.nombres, pe.apellido_paterno, pe.apellido_materno, tu.nombre as nombre_tipo_usuario');
      $this->db->from('usuario usu, persona pe, tipo_usuario tu');
      $this->db->where('usu.persona_id = pe.id AND usu.tipo_usuario_id = tu.id AND tu.nombre = "Conductor"');
      $this->db->order_by('usu.id', 'desc');
      return $this->db->get()->result_array();
    }


}
